OrgConfiguration__c CS = 	OrgConfiguration__c.getInstance();
List<Warehouse__c> warehouseInsertList = new List<Warehouse__c>();
List<Product_Table__c> prodTableList = new List<Product_Table__c>();

Map<Product_Table__c, Warehouse__c> productTableToWarehouseMap = new Map<Product_Table__c, Warehouse__c>();

for (Integer i = 0 ;i<10;i++){
    Product_Table__c pr = new Product_Table__c(Product_Name__c = 'Test'+i,Added_Date__c = Date.today());
    insert pr;
    prodTableList.add(pr);

}
for (Integer i = 0 ;i<10;i++){
    Warehouse__c warehouse = new Warehouse__c(Name ='Test'+i,Period_Start__c = Date.today(),Period_End__c = Date.today());
    insert warehouse;
}
for (Integer i = 0 ;i<3;i++){
    Warehouse__c warehouse = new Warehouse__c(Name ='Tester'+i,Period_Start__c = Date.today()+2,Period_End__c = Date.today()-12);
    insert warehouse;
}

for (Product_Table__c prodTableItem : prodTableList){
    if (prodTableItem.Added_Date__c == null){
        prodTableItem.Added_Date__c = Date.today();
    }
    for (Warehouse__c warehouseItem : [SELECT Period_Start__c,Period_End__c FROM Warehouse__c]){
        if (warehouseItem.Period_Start__c <= prodTableItem.Added_Date__c && warehouseItem.Period_End__c >= prodTableItem.Added_Date__c){
            prodTableItem.Warehouse__c = warehouseItem.Id;
        }
        else{
            Warehouse__c warehouse = new Warehouse__c();
            warehouse.Name = 'Warehouse' + prodTableItem.Added_Date__c +' '+prodTableItem.Added_Date__c
                    + CS.Period_Term__c;
            warehouseInsertList.add(warehouse);
            prodTableItem.Warehouse__c = warehouse.Id;
            productTableToWarehouseMap.put(prodTableItem,warehouse);

        }
    }
}
insert warehouseInsertList;
System.debug(prodTableList[0].Warehouse__c);

for (Product_Table__c prodTableItem : productTableToWarehouseMap.keySet()){
System.debug(prodTableItem.Warehouse__c);
